import React from "react";
import { Image, Text, TouchableOpacity, View } from "react-native";

function CategoryCard({ imgUrl, title }) {
  return (
    <TouchableOpacity>
      <Image
        source={{
          uri: imgUrl,
        }}
        className="h-20 w-20 rounded"
      />
      <Text>{title}</Text>
    </TouchableOpacity>
  );
}

export default CategoryCard;
